@js:
 merge_with_img(image, id, page, canvas) {
	var width = image.naturalWidth;
	var height = image.naturalHeight;
	var i = canvas.getContext("2d", {
		alpha: false
	});
	canvas.width = width;
	canvas.height = height;
	for (var count = get_corp_count(id, page), mod = parseInt(height % count), block_index = 0; block_index < count; block_index++) {
		var corp_height = Math.floor(height / count),
			p = corp_height * block_index,
			u = height - corp_height * (block_index + 1) - mod;
		0 == block_index ? corp_height += mod : p += mod, i.drawImage(image, 0, u, width, corp_height, 0, p, width, corp_height)
	}
}
function get_corp_count(id, page) {
	if (id.includes("cover")) {
		return 30;
	}
	var count = 10;
	var n = id + page;
	switch (n = (n = (n = java.md5Encode(n))
			.substr(-1))
		.charCodeAt(), n %= 10) {
		case 0:
			count = 44;
			break;
		case 1:
			count = 48;
			break;
		case 2:
			count = 52;
			break;
		case 3:
			count = 56;
			break;
		case 4:
			count = 60;
			break;
		case 5:
			count = 64;
			break;
		case 6:
			count = 68;
			break;
		case 7:
			count = 72;
			break;
		case 8:
			count = 76;
			break;
		case 9:
			count = 80
	}
	return count
}
const seed = [];
id = src.match(/\d+.jpg/)[0].replace('.jpg','')
seed.push(id)
const load_img = new Image();
load_img.src = src;
const merged_canvas = document.createElement('canvas');
merge_with_img(load_img, seed, 0, merged_canvas);
res = merged_canvas.toDataURL("image/png")
res=res.replace("data:image/png;base64,","")
java.base64Decode(res)

