ruleContent.content:
@js:
var html=result,
options={headers:{Referer:baseUrl}};

(function getImgList(){     
var p = /total\"\svalue=\"(\d+)/;     
var p1 = /content=\"(https:\/\/img.comicun.com.*?)\/(\d+).(\w+)\"/;
var mp = html.match(p1);
var jpg = html.match(p)[1];
var  jpf = mp[2];
var piclist = new Array(jpg); 
for (var i = 0 ; i < jpg ; i++) {
piclist[i] = mp[1]+"/"+(i+Number(jpf))+"."+mp[3];
}
return piclist; 
}()).map(uri=>
'<img src="'+uri+'">').join("\n")

